INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (6, 'Spargelpfanne', 'vegan, mit Chickenlike', 'i0gl7quurr0fnkja1nvi', '[{"id": "1", "content": "Chickenlike in Streifen schneiden und gleichmäßig mit der Speisestärke bestäuben. Butter in einer großen Pfanne erhitzen. Chickenlike darin von allen Seiten scharf anbraten, herausheben und beiseite stellen. "}, {"id": "2", "content": "Zwiebeln schälen und würfeln. Weißen Spargel ganz, grünen nur im unteren Drittel schälen, Enden kürzen. Spargelstangen in 2cm lange Stücke schneiden. Zwiebeln und Spargel im Restfett der Pfanne anbraten. \nGemüsebrühe und Crème Fraîche dazugeben. 3-5 Minuten köcheln lassen, dann das Chickenlike wieder dazu geben. Nochmals aufkochen, mit Salz und Pfeffer würzen. Die frisch gehackte Petersilie unterheben. Dazu passt Reis. "}]', NULL, NULL, 2, 'vegetarian');
