INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (7, 'Spinatknödel', '', 'fkdeeytswzliskqy2hmj', '[{"id": "1", "content": "Spinat ggf. auftauen und Wasser abgießen. "}, {"id": "2", "content": "Milch und Eier verquirlen, salzen, über das Knödelbrot gießen. Gut vermischen und 10 Minuten ziehen lassen. "}, {"id": "3", "content": "Zwiebel fein schneiden und in Butter hell anrösten, Spinat dazu und kurz dünsten. Mit Salz, Pfeffer, Muskat würzen. Am besten etwas abkühlen lassen. "}, {"id": "4", "content": "Knödelbrot, Spinat und Käse gut vermischen und mit nassen Händen Knödel formen. "}, {"id": "5", "content": "Knödel ca. 12 Minuten in Siebeinsatz über Wasserdampf ziehen lassen. "}, {"id": "6", "content": "Dazu passt: Salat mit Paprika, Gurke, Tomate, Salatkörner und Kernöl."}]', NULL, NULL, 2, 'vegetarian');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (8, 'Thai Suppe', 'mit Pak Choy, Paprika und Champignons', 'fxv1cb8gbtxsagfmufx4', '[{"id": "1", "content": "Eier wachsig weich kochen, ca 6,5 Minuten. "}, {"id": "2", "content": "Frühlingszwiebeln, Ingwer und Chili kleinschneiden und in Öl anbraten. Currypaste dazu, mit Gemüsebrühe ablöschen."}, {"id": "3", "content": "Das Zitronengras kreuzweise einschneiden und mit in die Suppe, 5 Minuten köcheln lassen. "}, {"id": "4", "content": "Kokosmilch und Gemüse hinzufügen."}, {"id": "5", "content": "Nudeln zubereiten, in Schälchen verteilen und mit der Suppe übergießen. Mit Ei und Koriander servieren.  "}]', NULL, '00:30:00', 2, 'vegetarian');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (9, 'Zuccinipfanne', '', 'wxjqmo5x6bmgf6ch7vug', '[{"id": "1", "content": "Olivenöl in einer großen Pfanne erhitzen und die Zwiebel darin anschwitzen, Tomatenmark dazu. Die Zucchini waschen, vierteln und in Scheiben schneiden, zusammen mit dem Knoblauch zu den Zwiebeln geben und mitbraten."}, {"id": "2", "content": "Die ungekochten Kritharaki hinzufügen und mit Gemüsebrühe ablöschen. "}, {"id": "3", "content": "Kurz aufkochen, mit Salz, Pfeffer, Thymian würzen und 12-15 Minuten auf kleiner Stufe köchen lassen, bis die Kritharaki gar sind. Dabei gelegentlich umrühren und bei Bedarf Flüssigkeit hinzufügen. "}, {"id": "4", "content": "Zum Schluss den gewürfelten Feta untermischen. \n"}]', NULL, NULL, 2, 'vegetarian');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (11, 'Zoodles mit Feta', '', 'zg5cbx2dndtducigptks', '[{"id": "1", "content": "Zucchini mit einem Spiralschneider zu \"Spaghetti\" verarbeiten."}, {"id": "2", "content": "Olivenöl in einer Pfanne auf mittlerer Stufe erhitzen. Knoblauch unter Rühren eine Minute anschwitzen. Zucchini-Spaghetti hinzufügen, Hitze etwas erhöhen. Tomaten zerkleinern und dazu geben."}, {"id": "3", "content": "Mit Zitrone, Salz und Pfeffer abschmecken. Feta zerkrümeln, die Hälfte unter die Zoodles heben und auf einen Teller geben. Mit dem restlichen Feta toppen."}]', NULL, NULL, 1, 'vegetarian');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (3, 'Hummusbrot', 'Mit gerösteten Kichererbsen', 'alqhxgsdcgnbqaz7t9z1', '[{"id": "1", "content": "Kichererbsen mit Olivenöl mischen und ca 25 Minuten im Ofen backen. Für die letzten 10 Minuten mit Paprika- und Knoblauchpulver vermischen."}, {"id": "2", "content": "Ruccola waschen, getrocknete Tomaten in Streifen schneiden."}, {"id": "3", "content": "Brot mit Humus bestreichen, mit Tomaten, Kichererbsen und Rucola belegen."}]', NULL, NULL, 2, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (28, 'Grünes Wokgemüse ', '', 'jjnpuhmt3h59gcrj1oqr', '[{"id": "1", "content": "Jasmin Reis kochen"}, {"id": "2", "content": "In der Zwischenzeit das Gemüse klein schneiden."}, {"id": "3", "content": "Ingwer, Knoblauch, Zwiebeln, Wasserkastanien und Bohnen im Wok anbraten."}, {"id": "4", "content": "Zum Schluss Schwarzkohl und Saucen dazu geben und kurz köcheln lassen."}]', NULL, NULL, 2, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (20, 'Mexikanischer Reisauflauf', 'Mit Paprika und schwarzen Bohnen', 'ktzz4naovnqtbw5yuead', '[{"id": "1", "content": "Den Reis garen, Ofen vorheizen."}, {"id": "2", "content": "Zwiebeln, Knoblauch würfeln und mit Olivenöl in einer ofenfesten gusseisernen Pfanne anbraten. Tomatenmark und Gewürze hinzufügen und kurz mit braten, gewürfelte Paprika hinzufügen. "}, {"id": "3", "content": "Bohnen und Mais hinzufügen und mit Gemüsebrühe aufgießen, köcheln lassen bis der Reis fertig ist. \nMit den Gewürzen abschmecken und Reis unterrühren."}, {"id": "4", "content": "Mit Mozzarella-Scheiben belegen und 10-15 im Ofen überbacken. Mit gehackten Frühlingszwiebeln und Koriandergrün servieren. "}]', '01:00:00', '00:15:00', 3, 'vegetarian');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (19, 'Nudeln mit Tomaten und Mozzarella', '', 'fjpwwcd0iqewof0opj84', '[{"id": "1", "content": "Nudeln kochen, Zwiebel fein schneiden, Tomaten halbieren. "}, {"id": "2", "content": "Zwiebeln in Olivenöl glasieren. Dann die Tomaten dazu, kurz anbraten und mit Salz und Pfeffer würzen. "}, {"id": "3", "content": "Gekochte Nudeln, klein-geschnittenen Mozzarella und Basilikum unterheben"}]', NULL, NULL, 2, 'vegetarian');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (16, 'Grüne Currysuppe mit Linsen', 'mit Süßkartoffel und Spinat', 'hvr7nsyfh4lbnuegbwny', '[{"id": "1", "content": "Zwiebeln, Knoblauch und Ingwer in feine Würfel schneiden und anbraten bis die Zwiebeln glasig sind.\nCurrypaste hinzufügen und kurz mitbraten, ca 2 Minuten."}, {"id": "2", "content": "Linsen hinzufügen und mit Brühe ablöschen. Aufkochen, dann ca 25 Minuten köcheln lassen. \nGewürfelte Süßkartoffel ca 5 Minuten bevor die Linsen gar sind dazugeben. "}, {"id": "3", "content": "Kokosmilch und Fischsauce einrühren, Spinat hinzufügen. Kurz umrühren, wenn der Spinat zusammenfällt mit Salz und Fischsauce abschmecken."}, {"id": "4", "content": "Mit Koriandergrün und Limettenspalten servieren. "}]', '00:40:00', '00:20:00', 2, 'fish');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (22, 'Miso-Tahini Dressing', '', 'luct7xowuohc8spa3m8e', '[{"id": "1", "content": "Wasser erhitzen. Chili kleinhacken (Menge nach Bedarf, wenig reicht!). Knoblauch und Ingwer in Stückchen schneiden. "}, {"id": "2", "content": "Alle Zutaten im Blender fein mixen."}, {"id": "3", "content": "Hält luftdicht verschlossen bis zu 3 Tage im Kühlschrank. "}]', '00:10:00', '00:10:00', 4, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (25, 'Linsen-Mangold-Curry', '', 'jzuxhsbgqme2pr6zjvqe', '[{"id": "1", "content": "Den Mangold putzen und waschen. Die Stiele würfeln und die Blätter in Streifen schneiden. Zwiebeln, Knoblauch und Ingwer schälen. Zwiebeln würfeln, Knoblauch und Ingwer hacken. Die Linsen in einem Sieb durchspülen und abtropfen lassen."}, {"id": "2", "content": "Die Butter erhitzen und Zwiebeln, Knoblauch und Ingwer darin glasig anschwitzen. Sesam, Kreuzkümmel und Kurkuma dazugeben und kurz mitbraten. Linsen, Mangold, Kokosmilch und Brühe dazugeben. Mit Salz würzen und alles etwa 15 Minuten bei mittlerer Hitze kochen"}, {"id": "3", "content": "Vor dem Servieren das Linsen-Mangold-Curry mit Salz und Zitronensaft abschmecken. "}]', '00:30:00', '00:15:00', 2, 'vegetarian');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (23, 'Miso Bowl mit Schwarzkohl', 'Aromatische Bowl mit Miso Dressing und gedämpftem Schwarzkohl', 'wtnachukp9d9v3lm24au', '[{"id": "1", "content": "Reis kochen, eine Portion Miso Dressing herstellen."}, {"id": "2", "content": "Den Schwarzkohl in einer Pfanne mit Tamari und Orangensaft ca 10 Minuten abgedeckt dünsten, bis der Kohl weich ist. "}, {"id": "3", "content": "Reis in Bowls aufteilen, mit Schwarzkohl, Rucola, Karotten und Paprika anrichten."}, {"id": "4", "content": "Tofuwürfel darübergeben und mit Miso Dressing verfeinern."}]', '00:25:00', '00:15:00', 2, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (17, 'Naan', '', 'wthrcl5igevjvuljdznq', '[{"id": "1", "content": "Hefe und Zucker in die lauwarme Milch rühren. Das Mehl in eine Schüssel geben, in die Mitte eine Mulde formen und die Hefemilch hineingießen. Mit etwas Mehl vom Rand vermischen und an einem warmen Ort 10 Minuten stehen lassen."}, {"id": "2", "content": "Joghurt, Salz und Backpulver mit in die Schüssel geben und den Teig gut 5 Minuten mit den Knethaken kneten. Schüssel mit einem Küchentuch abdecken und den Teig ca. 1h an einem warmen Ort gehen lassen."}, {"id": "3", "content": "Den Teig in ca. 6 gleich große Stücke teilen und Kugeln formen. Jede Kugel entweder mit den Händen (ähnlich wie eine Pizza) zu einem kleinen Fladen ziehen, der am Rand etwas dicker ist; oder klassisch auf etwas Mehl auswellen."}, {"id": "4", "content": "Eine beschichtete Pfanne sehr heiß werden lassen und das erste Naan hineingeben. Nach kurzer Zeit wenden, sobald es Blasen wirft, und von der anderen Seite backen. Vorsicht, die Brote werden schnell zu dunkel! Mit den restlichen Broten ebenso verfahren. Zum Servieren etwas flüssige Butter darübergeben."}]', '01:00:00', '00:20:00', 2, 'vegetarian');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (18, 'Kürbis-Kichererbsen Curry', '', 'kaq302yc6ac2dm61stge', '[{"id": "1", "content": "Kürbis in ca 3cm große Stücke, den Ingwer in Streifen schneiden. Koriandergrün abzupfen, die Stängel fein hacken. "}, {"id": "2", "content": "Öl erhitzen, Ingwer, Knoblauch, Chilli und Schalotten scharf anbraten, dann Hitze reduzieren. "}, {"id": "3", "content": "Wenn die Schalotten glasig sind Senfsaat, Curryblätter und Korianderstängel mitbraten, bis die Curryblätter knusprig werden. Kurkuma, Tomaten und Kokosmilch hinzufügen. Aufkochen, dann Kürbis und Kichererbsen hinzufügen. Bei niedriger Hitze mit Deckel für ca 45 Minuten köcheln lassen. \nBei Bedarf etwas Wasser hinzufügen. "}, {"id": "4", "content": "Nach Ablauf der Zeit den Deckel abnehmen und weitere 15 Minuten köcheln lassen, bis die Sauce cremig ist. Kurz vor Ende der Garzeit den Spinat unterheben. "}, {"id": "5", "content": "Mit Koriandergrün servieren. Dazu passt Reis oder Naan Brot. "}]', '01:15:00', '00:15:00', 4, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (15, 'Mexikanischer Eintopf', 'mit Bohnen, Karotten, Paprika und Schokolade!', 'pqddzmvyudmrx0km3fdd', '[{"id": "1", "content": "Die Paprika vierteln, entkernen und in feine Streifen schneiden. Die Karotten schälen, die Enden abschneiden und die Karotten ebenfalls in feine Streifen schneiden. Die Zwiebel schälen, halbieren und in feine Halbringe schneiden."}, {"id": "2", "content": "Paprika, Karotten und Zwiebeln in einem großen Topf 1-2Min. anbraten."}, {"id": "3", "content": "Gemüse mit den gehackten Tomaten ablöschen, Gewürzmischung hinzufügen und die Hitze auf mittlere Stufe reduzieren."}, {"id": "4", "content": "Die Bohnen und den Mais mit der Flüssigkeit sowie die Gemüsebrühe hinzugeben. Den Eintopf abgedeckt ca. 20–25Min. sanft köcheln lassen. Nach Bedarf etwas kochendes Wasser nachgießen, falls zu viel Flüssigkeit verköchelt."}, {"id": "5", "content": "Währenddessen die Petersilie samt Stängeln grob hacken. Kako hinzufügen. Den Eintopf nach Geschmack mit Gewürzmischung, Kako, Salz und Pfeffer abschmecken und mit Petersilie bestreut servieren."}]', '00:30:00', '00:10:00', 2, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (30, 'Polnischer Krautsalat', 'von Karin W.', '', '[{"id": "1", "content": "Spitzkohl, Möhren und Apfel raspeln. Zwiebel und Knoblauch fein schneiden, Paprika und Tomaten in dünne Scheiben schneiden. "}, {"id": "2", "content": "Gemüse mit den restlichen Zutaten vermengen, mit Salz und Pfeffer abschmecken und im Kühlschrank durchziehen lassen. "}]', NULL, NULL, 2, 'vegetarian');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (31, 'Gemüse Kartoffel Curry', '', 'ubildpertmpr9kqxgx3k', '[{"id": "1", "content": "Die Kartoffeln waschen, schälen und in grobe Würfel schneiden. Die Schalotten schälen und in Spalten schneiden. Zuckerschoten putzen und waschen. Ingwer schälen und fein reiben. Den Kreuzkümmel im Mörser zerreiben."}, {"id": "2", "content": "Das Erdnussöl in einer Pfanne erhitzen, Kartoffelwürfel und Schalotten darin goldgelb anbraten. Currypaste, Ingwer und Kreuzkümmel zugeben, kurz anrösten, Sahne und Gemüse fond dazugießen. Hitze reduzieren und alles 10-12 Minuten köcheln lassen."}, {"id": "3", "content": "Zuckerschoten und Erbsen zufügen und weitere 3-5 Minu ten köcheln lassen. Die Cashewkerne in einer Küchenma schine fein mahlen und unter das Curry rühren. Nochmals aufkochen. Alles mit Salz und Zucker abschmecken. Petersilie abbrausen und gut trocken schütteln. Blätter abzupfen, fein hacken und zuletzt in das Curry rühren."}, {"id": "4", "content": ""}]', NULL, NULL, 4, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (32, 'Colache', 'Mexikanischer Gemüsetopf', 'y7gihu9dex8lkq2mrftz', '[{"id": "1", "content": "Die Zucchini in Stücke, die Maiskolben in Scheiben schneiden. Die Bohnen putzen. Die Zwiebeln in Ringe schneiden, den Knoblauch fein hacken. "}, {"id": "2", "content": "Paprikaschoten in Streifen schneiden, die Tomaten in Stücke. Die Chillischote fein hacken. "}, {"id": "3", "content": "Butter in einem großen (gusseisernen) Topf erhitzen und das Gemüse darin andünsten. Mit Salz, Pfeffer und Chillipulver würzen. "}, {"id": "4", "content": "Das Ganze im Topf bei milder Hitze ca 10 Minuten köcheln lassen. "}, {"id": "5", "content": "Dazu passt Baguette. "}]', '00:30:00', '00:20:00', 4, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (36, 'Tofu & Brokkoli', 'Schnelles Essen in 15 Minuten', 'gxehczwvipiwtguk93qp', '[{"id": "1", "content": "Tofu in große Streifen schneiden und abtrocknen/auspressen. Salzen und Pfeffern. "}, {"id": "2", "content": "Tofu bei niedriger bis mittlerer Hitze in Öl anbraten, ca 4 Minuten pro Seite. "}, {"id": "3", "content": "Ingwer und Knoblauch hacken. Brokkoli kleinschneiden und blanchieren. "}, {"id": "4", "content": "Zutaten für die Sauce verrühren. "}, {"id": "5", "content": "Tofu beiseite stellen, in der Pfanne Ingwer und Knoblauch dünsten. Sauce dazu, kurz aufkochen. Bokkoli und gewürfeltes Tofu dazugeben. "}, {"id": "6", "content": "Mit Sesam bestreuen. Dazu passt Reis. "}]', '00:15:00', '00:15:00', 2, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (38, 'Spicy Tan Tan Ramen', '', '', '[{"id": "1", "content": "https://www.youtube.com/watch?v=VLfrRZoqzUg"}, {"id": "2", "content": "alternativ: https://www.edeka.de/rezepte/rezept/tantanmen-ramen-mit-veganem-hack.jsp"}]', NULL, NULL, 2, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (24, 'Gemüsetajine mit Sadri Reis', 'marokkanische Spezialität', 'ovmjly7yq61qsega3gjv', '[{"id": "1", "content": "Aubergine waschen. Zwiebeln und Knoblauch schälen. Alles in kleine Würfel scheneiden.\n\nZucchini und Paprika waschen und klein schneiden."}, {"id": "2", "content": "Herd auf hohe Stufe stellen. Olivenöl in der Tajine erhitzen. Auberginen, Zwiebeln und Knoblauch anbraten. Mit 1EL Ras el-hanout bestreuen.\n\nNach rund 5 Minuten Zucchini und Paprika zugeben. Feigen in Stücke schneiden und drüber geben. Nicht mehr rühren. Herd auf kleine Stufe stellen."}, {"id": "3", "content": "Tomaten waschen und vierteln.\n\nIn die Tajine geben."}, {"id": "4", "content": "Mangold waschen und klein schneiden.\n\nIn die Tajine geben.\n\n1EL Honig auf dem Gemüse verteilen. Restliches Ras el-Hanout zugeben.\n\nNicht rühren."}, {"id": "5", "content": "Tajine verschließen und Gemüse rund 40 Minuten garen lassen."}, {"id": "6", "content": "Reis zubereiten."}, {"id": "7", "content": "Deckel der Tajine abnehmen und rund 10 Minuten geöffnet weitergaren lassen.\n\nGemüse mit Sadri Reis anrichten."}]', '01:00:00', '00:15:00', 2, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (26, 'Erbsen-Guacamole', 'regionale Alternative', 'fy1phvvmmkafkerg4b0n', '[{"id": "1", "content": "Gib die Erbsen mit etwas Wasser in einen Topf und lasse sie rund fünf Minuten köcheln. Verwende nur so viel Wasser, dass die Erbsen nicht ganz bedeckt sind. Am besten eignen sich frische oder tiefgekühlte Erbsen."}, {"id": "2", "content": "Gieße das Kochwasser ab und stelle es für später zur Seite. Püriere die weich gekochten Erbsen. "}, {"id": "3", "content": "Hacke die Knoblauchzehe fein und schneide Zwiebel und Tomaten in feine Würfel. Hacke den Koriander und optional eine Chilischote ebenfalls grob."}, {"id": "4", "content": "Rühre das Öl, den Limettensaft, den Knoblauch sowie die Zwiebel- und Tomatenstücke unter das Erbsenpüree und schmecke mit Salz und Pfeffer ab. Füge so viel Kochwasser hinzu, bis die gewünschte Konsistenz erreicht ist. "}, {"id": "5", "content": "Lasse die Erbsen-Guacamole abkühlen. "}, {"id": "6", "content": "Tipp: Am besten schmeckt die Guacamole, wenn sie einige Stunden im Kühlschrank durchziehen konnte."}]', '00:45:00', '00:15:00', 2, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (27, 'Black Bean Tostadas', 'These toasted tortillas topped with flavorful black beans and fresh vegetables make a quick and delicious light meal. ', 'xpvf6ceprrk457b8blvb', '[{"id": "1", "content": "Bohnen einweichen und kochen. "}, {"id": "2", "content": "Backofen auf 170° vorheizen. Torillas nebeneinander auf ein Backblech geben. 15 Minuten knusprig backen. "}, {"id": "3", "content": "Währenddessen Zwiebel und Knoblauch kurz anbraten. Bohnen, Zitrone und Kreuzkümmel dazugeben. Ca 100ml Wasser dazugeben und 10 Minuten bei mittlerer Hitze braten. "}, {"id": "4", "content": "Bohnenmischung nach Geschmack etwas zermatschen, bei Bedarf Wasser hinzufügen. Mit Salz und Pfeffer abschmecken. "}, {"id": "5", "content": "Gebackene Tortillas mit Bohnenmus bestreichen. Salat, Frühlingszwiebeln, Tomaten und Koriander darauf verteilen. "}]', '00:15:00', '00:15:00', 2, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (29, 'Gemüse-Korma-Curry', 'mit Basmatireis und Mandelblättchen', 'wmfvwzcwhrl2ndqyqsgy', '[{"id": "1", "content": "Reis abspülen und kochen."}, {"id": "2", "content": "Währenddessen die Karotte ggf. schälen und schräg in dünne Scheiben schneiden. Die Enden der Bohnen abschneiden und die Bohnen halbieren oder dritteln. Die Kichererbsen in einem Sieb gut abtropfen lassen."}, {"id": "3", "content": "Die Mandeln in einer großen Pfanne ohne Zugabe von Fett bei mittlerer Hitze 1–2Min. anrösten. Vorsicht! Die Mandeln können schnell verbrennen! Zum Abkühlen aus der Pfanne nehmen, sie dunkeln sonst noch nach."}, {"id": "4", "content": "Die Karotten und die Bohnen in derselben Pfanne mit 1EL Pflanzenöl bei mittlerer Hitze 4–6Min. anbraten."}, {"id": "5", "content": "Die Kokosmilch unterrühren und je nach Schärfewunsch mit der Currypaste würzen. Das Curry auf niedrigster Stufe 4–5Min. sanft köcheln lassen. Mit Salz und Pfeffer abschmecken und die Kichererbsen untermischen."}, {"id": "6", "content": "Die Tomate in kleine Würfel schneiden, dabei den Strunk entfernen. Das Gemüse-Korma-Curry mit dem Reis anrichten und mit den Tomatenwürfeln und den Mandelblättchen garniert servieren."}]', NULL, NULL, 2, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (37, 'Kürbissuppe', '', '', '[{"id": "1", "content": "Kürbis, Möhren, Ingwer und Zwiebel schälen und würfeln, in der Butter andünsten. Mit der Brühe aufgießen und in etwa 15 - 20 Minuten weich kochen."}, {"id": "2", "content": "Dann sehr fein pürieren. Die Kokosmilch unterrühren, mit Salz, Pfeffer, Sojasauce, Curry und Zitronensaft abschmecken und noch mal erwärmen. Mit Korianderblättchen garniert servieren."}]', NULL, NULL, 6, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (33, 'Saftiger Zitronenkuchen', 'Rezept aus Österreich ', 'mkf8gvfyk09ti0r5eago', '[{"id": "1", "content": "Backrohr auf 170°C Ober-/Unterhitze vorheizen. Eine Kastenform von 24 cm Länge ausfetten und mit Zucker ausstreuen."}, {"id": "2", "content": "Weiche Butter, Puderzucker und Zitronenschale mit den Schneebesen eines Handrührgerätes oder einer Küchenmaschine cremig rühren. Nach und nach die Eigelb dazugeben und zu einer dickschaumigen Masse aufschlagen. Eiweiß steif schlagen, dabei den Zucker einrieseln lassen und so lange weiterschlagen, bis der Schnee schnittfest und glänzend ist. Mehl, Backpulver, und Stärkemehl mit einem Drittel des Schnees unter den Teig rühren, den restlichen Schnee locker und gleichmäßig unterheben."}, {"id": "3", "content": "Den Teig in die Kastenform einfüllen und im heißen Backrohr auf der unteren Schiene knapp 1 Stunde backen."}, {"id": "4", "content": "Den Kuchen in der Form etwas überkühlen lassen.\nZitronen auspressen und 2 Esslöffel Saft davon abnehmen. Den restlichen Saft mit 50g Puderzucker verrühren. Mit einem Schaschlikspieß in den warmen Kuchen einstechen und in diese Öffnungen die Zitronensaft-Zucker-Mischung hineinträufeln. So fortfahren, bis der Kuchen durch und durch gut getränkt ist.\nDen restlichen Staubzucker mit dem zurückgelassenen Zitronensaft und so viel Wasser verrühren, dass eine glatte glänzende Glasur entsteht. Den Kuchen damit überziehen."}, {"id": "5", "content": "Der saftige Zitronenkuchen schmeckt einen Tag nach dem Backen, also gut durchgezogen, am feinsten."}]', '01:30:00', '00:30:00', 1, 'vegetarian');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (34, 'Reisnudelsalat mit Limetten-Erdnuss-Dressing', 'Rezept von Klara', 'e51oujyllvybyopn0im7', '[{"id": "1", "content": "Rotkohl und Paprika in feine Streifen schneiden. Gurke längs halbieren, Kerne mit einem Löffel herauskratzen, anschließend zusammen mit der Karotte in feine Stifte schneiden. Geschnittenes Gemüse mit ¼ TL Salz würzen und leicht einmassieren."}, {"id": "2", "content": "Für das Dressing Knoblauch und Ingwer reiben und mit Erdnussmus, Sojasauce, Limettensaft, Essig, Sriracha, Sesamöl und 50 ml kaltem Wasser mit dem Schneebesen glatt rühren."}, {"id": "3", "content": "Reisnudeln in reichlich kochendem Wasser etwa 4–6 Minuten oder nach Packungsanweisung kochen. Anschließend abgießen, kurz abtropfen lassen und sofort mit dem Gemüse und dem Dressing vermengen."}, {"id": "4", "content": "Frühlingszwiebel und Chili in feine Ringe schneiden und unterheben. Kurz vor dem Servieren mit frisch gezupftem Koriandergrün und Erdnüssen toppen."}]', '00:15:00', '00:15:00', 2, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (35, 'Grünkohl-Süßkartoffel-Curry', 'veganes Curry mit Kichererbsen', 'yyk2vmkybufhzxoiwn9h', '[{"id": "1", "content": "Wasche den Grünkohl, entferne die harten und groben Stiele und schneiden den Grünkohl in kleinere Stücke.\nWasche die Süßkartoffeln und bürste sie ab, schneide sie dann in ein-Zentimeter-dicke Stifte. Lass die Kichererbsen durch ein Sieb abtropfen."}, {"id": "2", "content": "Entferne die Schale des Ingwers, indem du sie mit einem Teelöffel abschabst. Reibe den Ingwer oder hacke ihn fein. Halbiere die Chilischote, entferne die Kerne, wasche die Hälften und schneide sie klein. Schäle die Zwiebel und schneide sie klein."}, {"id": "3", "content": "Erhitze das Öl in einem Wok oder einer Pfanne, gib den Ingwer, die Chili, die Zwiebel und die Mandelstifte hinein und brate alles leicht an."}, {"id": "4", "content": "Füge das Currypulver hinzu und lass es kurz anschwitzen. Gieße dann die Gemüsebrühe und die Kokosmilch hinzu und bringe alles zum Kochen."}, {"id": "5", "content": "Füge den Grünkohl nach und nach hinzu und lasse alles zugedeckt bei mittlerer Hitze etwa 15 Minuten köcheln."}, {"id": "6", "content": "Füge die Süßkartoffeln und die Kichererbsen hinzu und lasse alles etwa zehn Minuten garen."}, {"id": "7", "content": "Wasche die Süßkartoffeln und bürste sie ab, schneide sie dann in ein-Zentimeter-dicke Stifte. Lass die Kichererbsen durch ein Sieb abtropfen."}, {"id": "8", "content": "Schmecke das Curry mit Sojasauce, Salz und Pfeffer ab.\nServiere das Curry alleine oder mit Reis. Garniere es mit den Frühlingszwiebeln."}]', NULL, NULL, 4, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (39, 'Schnelle Udon Nudeln', '', '', '[{"id": "1", "content": "Finely chop the garlic and ginger.  Slice the red onion and chop the broccolini into bite sized pieces. Cut off the base of the shimeji mushroom cluster to discard.  Set the mushrooms aside.  Chop the green onions into small batons "}, {"id": "2", "content": "Bring some water to boil for the noodles.  Soak the udon noodles for 3-4min.  Use chopsticks to gently loosen the noodles "}, {"id": "3", "content": "Make a stir fry sauce by combining the soy sauce, dark soy sauce, sriracha, plantbased oyster sauce, and toasted sesame oil "}, {"id": "4", "content": "Heat up a nonstick pan to medium heat.  Add the avocado oil followed by the garlic, ginger, and red onions.  Sauté for about 1min."}, {"id": "5", "content": "Add the broccolini and green onions.  Sauté for 2-3min. Add the shimeji mushrooms and sauté for about 1min. "}, {"id": "6", "content": "Strain out the udon noodles and add to the pan.  Add the stir fry sauce and sauté for another 2-3min."}, {"id": "7", "content": "Plate the noodles and sprinkle over some sesame seeds to serve."}, {"id": "8", "content": "https://www.youtube.com/watch?v=DOKvDbkJrdE"}]', '00:15:00', '00:15:00', 2, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (10, 'Tom Yum Suppe', 'Vegane Tom Yum mit Gemüse', 'wv3ro5hdctawz4o1ivj1', '[{"id": "1", "content": "Die Zwiebel in Scheiben schneiden und zusammen mit der Suppen Paste in einer großen Pfanne anschwitzen."}, {"id": "2", "content": "Das Gemüse schneiden und ebenfalls mit in die Pfanne geben.\n\nEtwa 2 Minuten braten lassen."}, {"id": "3", "content": "Die passierten Tomaten und die Kokosmilch dazu geben und köcheln lassen."}, {"id": "4", "content": "Wenn das Gemüse in der Suppe gar ist, kann man die Suppe auf zwei Schalen aufteilen und optional mit Reis oder Reisnudeln garnieren."}]', NULL, NULL, 2, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (12, 'Spargelrisotto mit Zitrone', '', 'wu5yo5vr4meie3nehk0a', '[{"id": "1", "content": "Den Spargel schälen, in 2 cm lange Stücke schneiden und in kochendem Wasser rund 5 Minuten ziehen lassen. Wasser leicht salzen und zuckern.\nMit einer Kelle schöpfen und beiseite stellen.\n500ml von dem Kochwasser aufbewahren und statt Brühe verwenden."}, {"id": "2", "content": "Die Zwiebeln klein schneiden und Öl in einem Topf erhitzen.\nZwiebeln in den Topf geben. Unter rühren nach gut zwei Minuten den Reis zugeben.\nKurz anrösten und mit Weißwein ablöschen."}, {"id": "3", "content": "Die Zitronenschale abreiben.\nPinienkerne ohne Fett in der Pfanne rösten.\nBeides beiseite stellen."}, {"id": "4", "content": "Nach und nach das Kochwasser vom Spargel zum Reis in den Topf geben.\nMit dem nächsten Schuss warten, bis der Reis die Flüssigkeit aufgenommen hat. Stets gut Rühren.\nWenn der Reis gar ist, den gekochten Spargel unterheben und drei Minuten ziehen lassen."}, {"id": "5", "content": "Kurz vor dem Servieren 1/3 des Parmesans unter den Reis heben.\nDann auf einem Teller anrichten. Nach belieben Salzen und Pfeffern.\nMit restlichem Parmesan, den Pinienkernen und der Zitronenschale anrichten."}]', NULL, NULL, 2, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (13, 'Gemüse Spintalasagne', 'Comfortfood: vegan oder vegetarisch', 'uopckgclc7ubbgzmnflx', '[{"id": "1", "content": "Die Zwiebeln und Champignons in kleine Würfel schneiden. Die Karotten raspeln. \n\nDen Spinat in grobe Streifen schneiden oder falls TK Spinat in der Mikrowelle auftauen und danach überschüssige Flüssigkeit abgiessen."}, {"id": "2", "content": "Die Zwiebeln in einer Pfanne mit der Butter braten bis sie glasig sind.\n\nDann die Karotten und Champignons dazu geben und mit mehr Hitze anbraten."}, {"id": "3", "content": "Nun die Milch und Gemüsebrühe unterühren. \n\nDanach den Spinat hinzufügen und kochen bis er zerfällt (Bei TK Spinat nicht nötig)."}, {"id": "4", "content": "Die Knoblauchzehe pressen und beimengen und den Frischkäse unterühren.\n\nZum Schluss mit Salz, Pfeffer und milden Chilipulver abschmecken. Es darf ruhig würzig und salzig sein.\n\nDie Füllung kann nun vom Herd an die Seite gestellt werden."}, {"id": "5", "content": "Es bietet sich an nun den Backofen auf 180-200 Grad Umluft zu stellen."}, {"id": "6", "content": "Das Öl in einem Topf erhitzen (nicht zu heiß!) und das Mehl mit einem Schneebesen nach und nach unterühren.\n\nDanach die passierten Tomaten nach und nach unterühren."}, {"id": "7", "content": "Zum Schluss die Sojasauce unterühren und die Knoblauchzehe dazu pressen. \n\nDas ganze mit Salz und Pfeffer abschmecken."}, {"id": "8", "content": "In einer Auflaufform abwechselnd Spinatfülle, Tomatensauce und Lasagneplatten stapeln.\n\nDie letzte Schicht mit Spinat oder Tomatensauce beenden und den geriebenen Käse darüber verteilen."}, {"id": "9", "content": "Das ganze für circa 25 Minuten im Ofen backen."}]', '01:30:00', '01:00:00', 3, 'vegan');
INSERT INTO recipe (id, name, description, image, steps, cook_time, prep_time, portions, diet) VALUES (21, 'JJajangmyeon', 'Kroean-Chinese black noodles', 'etvclvslp48jkm2ucvek', '[{"id": "1", "content": "Kombinieren Sie in einem Messbecher heißes Wasser und die getrockneten Shiitake-Pilze und lassen Sie sie 15 bis 20 Minuten ruhen. In der Zwischenzeit alles Gemüse schneiden."}, {"id": "2", "content": "Zu diesem Zeitpunkt werden die Shiitake-Pilze erweicht. Drücken Sie das gesamte Wasser aus dem Shiitake (stellen Sie sicher, dass Sie den gesamten Saft in den Messbecher sammeln) und hacken Sie ihn. Gießen Sie mehr Wasser in die Shiitake-Pilzbrühe, um eine genaue 1 Tasse zu erhalten."}, {"id": "3", "content": "Einen großen Wok oder eine Pfanne bei mittlerer bis hoher Hitze erhitzen. Fügen Sie Speiseöl, Knoblauch, Ingwer und Frühlingszwiebeln hinzu. 45 Sekunden bis 1 Minute braten, um diese Aromen in das Öl zu geben."}, {"id": "4", "content": "Wenn Sie alle Düfte riechen können, fügen Sie schwarze Bohnenpaste hinzu und rühren Sie 1 Minute lang. Ständig rühren."}, {"id": "5", "content": "Den Rest der Gemüsezwiebeln, Kohl, Zucchini und Pilze hinzufügen. Alles zusammenrühren und 3 bis 4 Minuten braten oder bis das Gemüse weich ist. Häufig umrühren."}, {"id": "6", "content": "Fügen Sie jetzt die Shiitake-Pilzbrühe, den Zucker und die vegetarische Austernsauce hinzu. Alles mischen, zum Kochen bringen, Hitze auf mittel reduzieren und 10 bis 15 Minuten ohne Abdeckung köcheln lassen. Gelegentlich umrühren, damit das Gemüse gleichmäßig kocht."}, {"id": "7", "content": "In der Zwischenzeit ist dies der perfekte Zeitpunkt, um mit dem Kochen Ihrer Nudeln zu beginnen. Sie können jede Ihrer Lieblingsnudeln verwenden, oder sogar Reis passt hervorragend zu dieser Jjajang-Sauce. Kochen Sie die Nudeln, indem Sie den Anweisungen der von Ihnen verwendeten Verpackung folgen. Nehmen Sie die gekochten Nudeln aus dem Topf und spülen Sie sie unter kaltem Eiswasser aus, um das Kochen zu beenden und die Stärke loszuwerden. Vollständig abtropfen lassen."}, {"id": "8", "content": "Mischen Sie Maisstärke und die gleiche Menge kaltes Wasser. Mit kreisender Bewegung in den Wok gießen und sofort umrühren. Fügen Sie Ihrem Geschmack schwarzen oder weißen Pfeffer hinzu."}, {"id": "9", "content": "Gießen Sie die Jjajang-Sauce über die gekochten Nudeln. Füllen Sie es mit ein paar gelobten Gurken auf, wenn Sie möchten! Mischen Sie gut, bevor Sie essen und genießen!!"}, {"id": "10", "content": ""}]', NULL, NULL, 4, 'vegan');
